var marked = require('marked');
var fs = require('fs');

// Find original markdown files and define character set
var readMe = fs.readFileSync('../README.md', 'utf-8');
var index = fs.readFileSync('../index.md', 'utf-8');

// Convert markdown files to html
var markdownReadMe = marked(readMe);
var markdownIndex = marked(index);

// Set path of new files with compiled html code 
fs.writeFileSync('../README.html', markdownReadMe);
fs.writeFileSync('../index2.html', markdownIndex);

